import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { map } from 'rxjs/operators';
import { HttpClient } from '@angular/common/http';

// CONFIG
import { environment } from '../../../environments/environment';

// MODEL
import { Area } from '../../domain/gestione_certificati_db/area';

/**
 * THIS SERVICE MAKE HTTP REQUEST TO SERVER, FOR CUSTOMIZE IT EDIT ../area.service.ts
 */

/*
 * SCHEMA DB area
 *
	{
		nome: {
			type: 'String',
			required : true,
			unique : true, 
		},
		//RELATIONS
		//EXTERNAL RELATIONS
		area: {
			type: Schema.ObjectId,
			required : true,
			ref : "voucher"
		},
	}
 *
 */
@Injectable()
export class AreaBaseService {

    contextUrl: string = environment.endpoint + '/aree';
    constructor(
        protected http: HttpClient
        ) { }

    // CRUD METHODS

    /**
    * areaService.create
    *   @description CRUD ACTION create
    *
    */
    create(item: Area): Observable<Area> {
        return this.http
            .post<Area>(this.contextUrl, item)
            .pipe(map(data => data));
    }

    /**
    * areaService.get
    *   @description CRUD ACTION get
    *   @param ObjectId id Id 
    *
    */
    get(id: string): Observable<Area> {
        return this.http
            .get<Area>(this.contextUrl + '/' + id)
            .pipe(map(data => data));
    }

    /**
    * areaService.list
    *   @description CRUD ACTION list
    *
    */
    list(): Observable<Area[]> {
        return this.http
            .get<Area[]>(this.contextUrl)
            .pipe(map(data => data));
    }

    /**
    * areaService.update
    *   @description CRUD ACTION update
    *   @param ObjectId id Id
    *
    */
    update(item: Area): Observable<Area> {
        return this.http
            .post<Area>(this.contextUrl + '/' + item._id, item)
            .pipe(map(data => data));
    }


    // Custom APIs

}
